import React from 'react';

import useStyles from './styles'
import {Card, CardActions, CardContent, CardMedia, Button, Typography} from "@material-ui/core";
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt'; //like icon
import DeleteOutlineIcon from '@material-ui/icons/DeleteOutline';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import moment from 'moment';
import FileResizer from "react-image-file-resizer";



const Post = ({ post, setCurrentId }) => {
 // const dispatch = useDispatch();
  const classes = useStyles();
  const resizeFile = (file) => new Promise(resolve => {
    FileResizer.imageFileResizer(file, 300, 300, 'JPEG', 100, 0,
     uri => {
       resolve(uri);
     },
     'base64'
    );
  });
  const resize = async (file) => {
    const image = await resizeFile(file);
    return image;
  }
  return (
   <Card className={classes.card}>
     <CardMedia className={classes.media}
                onChange={() => resize(post.selectedFile)}
                image={post.selectedFile}
                title={post.title}
     />
     <div className={classes.overlay}>
       <Typography variant="h6">{post.creator}</Typography>
       <Typography variant="body2">{moment(post.createdAt).fromNow()}</Typography>
     </div>
     <div className={classes.overlay2}>
       <Button style={{ color: 'white' }} size="small" onClick={() => setCurrentId(post._id)}><MoreHorizIcon fontSize="default" /></Button>
     </div>
     <div className={classes.details}>
       <Typography variant="body2" color="textSecondary" component="h2">{post.tags.map((tag) => `#${tag} `)}</Typography>
     </div>
     <Typography className={classes.title} gutterBottom variant="h5" component="h2">{post.title}</Typography>
     <CardContent>
       <Typography variant="body2" color="textSecondary" component="p">{post.message}</Typography>
     </CardContent>
     <CardActions className={classes.cardActions}>
       <Button size="small" color="primary" onClick={() => {}}><ThumbUpAltIcon fontSize="small" /> Like {post.likeCount} </Button>
       <Button size="small" color="primary" onClick={() => {}}><DeleteOutlineIcon fontSize="small" /> Delete</Button>
     </CardActions>
   </Card>
  );
};

export default Post;
